#!/usr/bin/env node

/**
 * Task 131 Screenshot Capture
 * Automatically executed by tasks/runner.sh nightly automation
 * Generates screenshots for OMSiteSurvey component
 */

const ScreenshotUtility = require('../../scripts/screenshot-utility');

const task131Screenshots = [
  { 
    route: '/admin/tools/survey', 
    description: 'OMSiteSurvey Main Interface with Access Control' 
  },
  { 
    route: '/admin/tools/survey?tab=filesystem', 
    description: 'Filesystem Analysis Tab with File Scanning Results' 
  },
  { 
    route: '/admin/tools/survey?tab=menu', 
    description: 'Menu Audit Tab with Role-based Visibility Analysis' 
  },
  { 
    route: '/admin/tools/survey?tab=roles', 
    description: 'User Role Matrix and Access Control Analysis' 
  }
];

console.log('üéØ Starting Task 131 Screenshot Capture via Nightly Runner');
console.log('üì∏ Target: OMSiteSurvey Component Screenshots');

const utility = new ScreenshotUtility({
  headless: true,
  baseUrl: process.env.[REDACTED] || 'http://localhost:3000',
  username: process.env.[REDACTED] || 'admin',
  password: process.env.[REDACTED] || 'admin123',
  screenshotDir: '/var/www/orthodox-church-mgmt/orthodoxmetrics/prod/screenshots'
});

utility.run(131, task131Screenshots)
  .then(({ summary, failed }) => {
    console.log('‚úÖ Task 131 Screenshot Capture Complete');
    console.log(`üìä Generated: ${summary.length} screenshots`);
    console.log(`‚ùå Failed: ${failed.length} screenshots`);
    
    if (failed.length > 0) {
      console.log('‚ùå Failed screenshots:', failed.map(f => f.description));
      process.exit(1);
    }
    
    console.log('üéâ All Task 131 screenshots generated successfully!');
    process.exit(0);
  })
  .catch(error => {
    console.error('‚ùå Task 131 Screenshot Capture Failed:', error.message);
    process.exit(1);
  });
