#!/usr/bin/env node

/**
 * Task 132 Screenshot Capture  
 * Automatically executed by tasks/runner.sh nightly automation
 * Generates screenshots for Blog System functionality
 */

const ScreenshotUtility = require('../../scripts/screenshot-utility');

const task132Screenshots = [
  { 
    route: '/admin/tools/page-editor', 
    description: 'Enhanced PageEditor with Content Type Toggle (Blog/Page)' 
  },
  { 
    route: '/admin/tools/page-editor?type=blog', 
    description: 'Blog Creation Interface with Author and Publishing Controls' 
  },
  { 
    route: '/admin/blog-admin', 
    description: 'Blog Administration Dashboard with Management Features' 
  },
  { 
    route: '/blog', 
    description: 'Public Blog Feed with Post Cards and Navigation' 
  }
];

console.log('üéØ Starting Task 132 Screenshot Capture via Nightly Runner');
console.log('üì∏ Target: Blog System Component Screenshots');

const utility = new ScreenshotUtility({
  headless: true,
  baseUrl: process.env.[REDACTED] || 'http://localhost:3000',
  username: process.env.[REDACTED] || 'admin', 
  password: process.env.[REDACTED] || 'admin123',
  screenshotDir: '/var/www/orthodox-church-mgmt/orthodoxmetrics/prod/screenshots'
});

utility.run(132, task132Screenshots)
  .then(({ summary, failed }) => {
    console.log('‚úÖ Task 132 Screenshot Capture Complete');
    console.log(`üìä Generated: ${summary.length} screenshots`);
    console.log(`‚ùå Failed: ${failed.length} screenshots`);
    
    if (failed.length > 0) {
      console.log('‚ùå Failed screenshots:', failed.map(f => f.description));
      process.exit(1);
    }
    
    console.log('üéâ All Task 132 screenshots generated successfully!');
    process.exit(0);
  })
  .catch(error => {
    console.error('‚ùå Task 132 Screenshot Capture Failed:', error.message);
    process.exit(1);
  });
