const session = require('express-session');
const MySQLStore = require('express-mysql-session')(session);

// Load environment variables
require('dotenv').config();

console.log('üîß Initializing BULLETPROOF session configuration...');
console.log('   NODE_ENV:', process.env.[REDACTED]);
console.log('   DB_HOST:', process.env.[REDACTED] || 'localhost');
console.log('   DB_NAME:', process.env.[REDACTED] || 'orthodoxmetrics_db');

// Database connection options for session store
const sessionStoreOptions = {
  host: process.env.[REDACTED] || 'localhost',
  port: process.env.[REDACTED] || 3306,
  user: process.env.[REDACTED] || 'orthodoxapps',
  password: process.env.[REDACTED] || 'Summerof1982@!',
  database: process.env.[REDACTED] || 'orthodoxmetrics_db',
  charset: 'utf8mb4',
  // Session store specific settings
  expiration: 86400000, // 24 hours in milliseconds
  checkExpirationInterval: 900000, // Check every 15 minutes
  createDatabaseTable: true,
  endConnectionOnClose: true,
  // üîß DEBUG: Limit concurrent sessions for troubleshooting
  clearExpired: true,
  schema: {
    tableName: 'sessions',
    columnNames: {
      session_id: 'session_id',
      expires: 'expires',
      data: 'data'
    }
  }
};

// Create session store
const store = new MySQLStore(sessionStoreOptions);

// Handle store events with enhanced debugging
store.on('error', (error) => {
  console.error('‚ùå Session store error:', error);
});

store.on('connect', () => {
  console.log('‚úÖ Session store connected to MySQL');
});

// üîß DEBUG: Add session debugging hooks
const originalGet = store.get.bind(store);
const originalSet = store.set.bind(store);
const originalDestroy = store.destroy.bind(store);

store.get = function(sessionId, callback) {
  console.log(`üîç SESSION GET: ${sessionId}`);
  return originalGet(sessionId, (err, session) => {
    if (err) {
      console.log(`‚ùå SESSION GET ERROR: ${sessionId} - ${err.message}`);
    } else if (session) {
      console.log(`‚úÖ SESSION FOUND: ${sessionId} - User: ${session.user?.email || 'No user'}`);
    } else {
      console.log(`üîç SESSION NOT FOUND: ${sessionId}`);
    }
    callback(err, session);
  });
};

store.set = function(sessionId, session, callback) {
  console.log(`üíæ SESSION SET: ${sessionId} - User: ${session.user?.email || 'No user'}`);
  return originalSet(sessionId, session, callback);
};

store.destroy = function(sessionId, callback) {
  console.log(`üóëÔ∏è  SESSION DESTROY: ${sessionId}`);
  return originalDestroy(sessionId, callback);
};

// Session configuration
const isProduction = process.env.[REDACTED] === 'production';
const sessionSecret = process.env.[REDACTED] || 'orthodox-metrics-secret-2025';

console.log('üç™ Session settings:');
console.log('   Production mode:', isProduction);
console.log('   Session secret:'[REDACTED]'SET' : 'NOT SET');
console.log('   Secure cookies:', false); // Temporarily disabled for debugging
console.log('   Session store:', 'MySQL');

const sessionConfig = {
  name: 'orthodox.sid', // Session cookie name
  secret: sessionSecret,
  store: store,
  resave: false, // Don't save session if unmodified
  saveUninitialized: false, // Don't create session until something stored
  rolling: true, // Extend session on each request
  cookie: {
    secure: false, // TEMPORARILY DISABLED - set to true for production HTTPS
    httpOnly: true, // Prevent XSS attacks
    maxAge: 86400000, // 24 hours
    sameSite: 'lax', // Allow same-site requests
    // domain: undefined // Let browser determine domain automatically
  }
};

console.log('üéØ Creating session middleware...');
const sessionMiddleware = session(sessionConfig);

// üîß DEBUG: Wrap session middleware to log cookie transmission
const debugSessionMiddleware = (req, res, next) => {
  const originalSessionId = req.sessionID;
  
  console.log(`üç™ COOKIE DEBUG - ${req.method} ${req.path}`);
  console.log(`   Raw cookies: ${req.headers.cookie || 'NONE'}`);
  console.log(`   Session ID before: ${originalSessionId || 'NONE'}`);
  
  // üîß FIX: Check for duplicate session cookies
  if (req.headers.cookie) {
    const cookies = req.headers.cookie.split(';');
    const sessionCookies = cookies.filter(cookie => cookie.trim().startsWith('orthodox.sid='));
    if (sessionCookies.length > 1) {
      console.log(`‚ö†Ô∏è  WARNING: ${sessionCookies.length} duplicate session cookies detected!`);
      sessionCookies.forEach((cookie, i) => {
        const value = cookie.split('=')[1]?.substring(0, 30) + '...';
        console.log(`   Cookie ${i+1}: ${value}`);
      });
    }
  }
  
  sessionMiddleware(req, res, (err) => {
    if (err) {
      console.log(`‚ùå Session middleware error: ${err.message}`);
      return next(err);
    }
    
    const newSessionId = req.sessionID;
    console.log(`   Session ID after: ${newSessionId || 'NONE'}`);
    console.log(`   Session user: ${req.session?.user?.email || 'NONE'}`);
    console.log(`   üîç Session ${originalSessionId === newSessionId ? 'REUSED' : 'NEW/CHANGED'}`);
    
    // üîß FIX: Clear any duplicate cookies on response
    if (req.session && req.session.user) {
      res.clearCookie('orthodox.sid', { path: '/', domain: undefined });
      res.clearCookie('orthodox.sid', { path: '/', domain: '.orthodoxmetrics.com' });
      res.clearCookie('orthodox.sid', { path: '/', domain: 'orthodoxmetrics.com' });
    }
    
    next();
  });
};

// Export both the middleware and store for use in other modules
module.exports = {
  sessionMiddleware: debugSessionMiddleware,
  sessionStore: store,
  isProduction
};

console.log('‚úÖ Session configuration complete!'); "
