// server/config/session.js - FRONTEND-COMPATIBLE SESSION CONFIG
const session = require('express-session');
const MySQLStore = require('express-mysql-session')(session);

// Load environment variables
require('dotenv').config();

// Database connection options for session store
const sessionStoreOptions = {
  host: process.env.[REDACTED] || 'localhost',
  port: process.env.[REDACTED] || 3306,
  user: process.env.[REDACTED] || 'orthodoxapps',
  password: process.env.[REDACTED] || 'Summerof1982@!',
  database: process.env.[REDACTED] || 'orthodoxmetrics_db',
  charset: 'utf8mb4',
  expiration: 86400000, // 24 hours
  checkExpirationInterval: 900000, // Check every 15 minutes
  createDatabaseTable: true,
  endConnectionOnClose: true,
  clearExpired: true,
  schema: {
    tableName: 'sessions',
    columnNames: {
      session_id: 'session_id',
      expires: 'expires',
      data: 'data'
    }
  }
};

const store = new MySQLStore(sessionStoreOptions);

store.on('error', (error) => {
  console.error('‚ùå Session store error:', error);
});

store.on('connect', () => {
  console.log('‚úÖ Session store connected successfully');
});

const sessionSecret = process.env.[REDACTED] || 'orthodox-metrics-production-secret-2025';

console.log('üç™ Session configuration:');
console.log('   Environment:', process.env.[REDACTED] || 'development');
console.log('   Session secret:'[REDACTED]'SET' : 'NOT SET');

module.exports = session({
  name: 'connect.sid',
  secret: sessionSecret,
  store: store,
  resave: false,
  saveUninitialized: false,
  rolling: true,
  proxy: true,
  cookie: {
    secure: false, // üîß FIXED: Allow non-HTTPS for development/testing
    httpOnly: false, // üîß FIXED: Allow JavaScript access for frontend
    maxAge: 86400000, // 24 hours
    sameSite: 'lax', // üîß FIXED: Allow cross-origin requests
    path: '/', // üîß FIXED: Available for all paths
    // No domain restriction for better compatibility
  }
});
